create table public.seat_availability_mapping (
  id bigint generated by default as identity not null,
  available_seats integer not null,
  booked_seats character varying(255) null,
  journey_date date not null,
  total_seats integer not null,
  bus_id integer not null,
  constraint seat_availability_mapping_pkey primary key (id),
  constraint uk4doicqx40q6m74kqgcbf3ylna unique (bus_id, journey_date),
  constraint fkrfg4g20f9elvq6po5y8njaxix foreign KEY (bus_id) references bus (bus_id)
) TABLESPACE pg_default;

-- 1. Clear existing data
DELETE FROM seat_availability_mapping;

-- 2. Insert new data
-- Create a helper function in Supabase for date generation (PostgreSQL CTE)

DO $$
DECLARE
  d DATE;
  b RECORD;
  layout_json TEXT;
  seat_labels TEXT[];
  total_seats INT;
BEGIN
  FOR b IN SELECT * FROM bus WHERE layout_id IS NOT NULL LOOP
    -- Get all seat labels from the layout
    SELECT layout::jsonb INTO layout_json
    FROM seat_layout
    WHERE layout_id = b.layout_id;

    SELECT array_agg(seat ORDER BY seat) INTO seat_labels
    FROM jsonb_array_elements(layout_json::jsonb) AS row,
         jsonb_array_elements_text(row) AS seat
    WHERE seat IS NOT NULL AND seat <> '';

    total_seats := array_length(seat_labels, 1);

    FOR i IN 0..14 LOOP
      d := CURRENT_DATE + i;

      INSERT INTO seat_availability_mapping (
        bus_id,
        journey_date,
        total_seats,
        available_seats,
        booked_seats
      )
      VALUES (
        b.bus_id,
        d,
        total_seats,
        total_seats,
        '{}'::jsonb
      );
    END LOOP;
  END LOOP;
END $$;

